(25, 'Metric Alloy Steel Hex Drive Flat Head Screws', '<div class="CpyCntnr" style="width:428px;"><p class="PrsnttnCpy verticalGutter">These are among our strongest flat head <span class="Wrd">screws.</span> <span class="Wrd">Theyre</span> angled under the head to sit flush within countersunk <span class="Wrd">holes.</span> Length is measured from the top of the <span class="Wrd">head.</span> <span class="Wrd">Note:</span> Not recommended for high-strength <span class="Wrd">fastening.</span></p><p class="PrsnttnCpy verticalGutter"><span class="EmphasizedTxt">Black</span><span class="EmphasizedTxt">-</span><span class="EmphasizedTxt">oxide</span> alloy steel screws have mild corrosion resistance in dry <span class="Wrd">environments.</span></p><p class="PrsnttnCpy verticalGutter"><span class="EmphasizedTxt">Zinc</span><span class="EmphasizedTxt">-</span><span class="EmphasizedTxt">plated</span> alloy steel screws resist corrosion in wet <span class="Wrd">environments.</span> The screws with a <span class="EmphasizedTxt">blue</span><span class="EmphasizedTxt">-</span><span class="EmphasizedTxt">dyed</span> finish are easy to <span class="Wrd">distinguish.</span></p><p class="PrsnttnCpy"><img class="Prsnttn_CADImg" src="https://images1.mcmaster.com/mvB/Contents/gfx/CADIconForCpy.png?ver=1452869604" width="22" height="19" alt="For technical drawings and 3-D models, click on a part number." />For technical drawings and 3-D models, click on a part number.</p></div>', '1492', '238,125,128,129,131,132,133,134,136,137,138,139,140,141,142,152,153,154,155,156,157,158,160,161,162,163,164,165,166,167,168,169,170,171,172,173,174,175,177,178,180,181,116,117,118,119,120,195,240,121,122,199,241,201,202,203,204,242,206,243,208,209,215,216', 'https://images1.mcmaster.com/mvB/contents/gfx/small/91253a153c2-b01-digitals.png?ver=1455633582;https://images1.mcmaster.com/mvB/contents/gfx/small/91253a155c2-b01s.png?ver=1469603542'),
(26, 'Mil.  Spec.  Alloy Steel Hex Drive Flat Head Screws', '<div class="CpyCntnr" style="width:485px;"><p class="PrsnttnCpy verticalGutter">Choose these screws for their adherence to strict military standards for material and <span class="Wrd">construction.</span> Cadmium plating provides corrosion resistance in wet <span class="Wrd">environments.</span> Screws are angled under the head to sit flush within countersunk <span class="Wrd">holes.</span> Length is measured from the top of the <span class="Wrd">head.</span></p><p class="PrsnttnCpy verticalGutter"><span class="Wrd">Note:</span> Not recommended for high-strength <span class="Wrd">fastening.</span></p><p class="PrsnttnCpy"><img class="Prsnttn_CADImg" src="https://images1.mcmaster.com/mvB/Contents/gfx/CADIconForCpy.png?ver=1452869604" width="22" height="19" alt="For technical drawings and 3-D models, click on a part number." />For technical drawings and 3-D models, click on a part number.</p></div>', '1493', '238,2,10,14,16,21,23,25,61,63,65,67,68,70,116,118,219,121,122,124,220', 'https://images1.mcmaster.com/mvB/contents/gfx/small/caption-97595a219c1-b01s.png?ver=1455633609'),
