INSERT INTO `products` VALUES ('2', 'Metric Alloy Steel Socket Head Screws', '<div class=\"CpyCntnr\" style=\"width:602px;\"><p class=\"PrsnttnCpy verticalGutter\">These screws are made from an alloy steel <span class=\"Wrd\">thats</span> stronger than Grade 8 <span class=\"Wrd\">steel.</span> Length is measured from under the <span class=\"Wrd\">head.</span></p><p class=\"PrsnttnCpy verticalGutter\"><span class=\"EmphasizedTxt\">Black</span><span class=\"EmphasizedTxt\">-</span><span class=\"EmphasizedTxt\">oxide</span> steel screws are mildly corrosion resistant in dry <span class=\"Wrd\">environments.</span></p><p class=\"PrsnttnCpy verticalGutter\"><span class=\"EmphasizedTxt\">Zinc</span><span class=\"EmphasizedTxt\">-</span><span class=\"EmphasizedTxt\">plated</span> steel screws are more corrosion resistant than black-oxide screws for use in wet <span class=\"Wrd\">environments.</span> The screws with a <span class=\"EmphasizedTxt\">blue</span><span class=\"EmphasizedTxt\">-</span><span class=\"EmphasizedTxt\">dyed</span> finish are easy to <span class=\"Wrd\">distinguish.</span></p><p class=\"PrsnttnCpy verticalGutter\"><span class=\"EmphasizedTxt\">Zinc</span><span class=\"EmphasizedTxt\">-</span><span class=\"EmphasizedTxt\">flake</span><span class=\"EmphasizedTxt\">-</span><span class=\"EmphasizedTxt\">coated</span> steel screws are 20 times as corrosion resistant as zinc-plated screws and comparable to Dacromet-coated <span class=\"Wrd\">screws.</span></p><p class=\"PrsnttnCpy verticalGutter\"><span class=\"EmphasizedTxt\">Zinc</span><span class=\"EmphasizedTxt\">-</span><span class=\"EmphasizedTxt\">aluminum</span><span class=\"EmphasizedTxt\">-</span><span class=\"EmphasizedTxt\">coated</span> steel screws are 40 times as corrosion resistant as zinc-plated screws and are also known as Magni-coated <span class=\"Wrd\">screws.</span> They withstand <span class=\"Wrd\">1,</span>000 hours of salt spray per ASTM <span class=\"Wrd\">B117.</span></p><p class=